codeSample.code:
DO_User _user;
long IDUser = 0L;

_user = new DO_User();
_user.Login = "fmonteiro";
_user.Password = "password";
_user.insObject(true); // assigning selectIdentity parameter to true retrieves Sequence for IDUser
Console.WriteLine(
  "user inserted, at:\nIDUser = {0}", 
  IDUser = _user.IDUser
);
_user.Dispose(); _user = null;

_user = new DO_User();
if (
  _user.getObject(IDUser) // based on it's PK
) {
  Console.WriteLine(
    "successully read Fields for User: {0}\n\tlogin: {1}", 
    _user.IDUser, 
    _user.Login
  );
} else {
  Console.WriteLine(
    "couldn't read Fields for User: {0}"
    _user.IDUser
  );
}

//_user.getObject(IDUser); // comment, no need if you've done it already
_user.Login = "francisco";
_user.updObject(false); // assigning forceUpdate parameter to false Updates only if changes have been made

DO_User _user = new DO_User();
if (_user.getObject_byLogin(
  "fmonteiro", 
  "password"
)) {
  Console.WriteLine(
    "user {0} successfully retrieved", 
    _user.IDUser
  );
} else {
  Console.WriteLine("couldn't retrieved User");
}

long IDGroup;
long IDUser;


// Finding IDUser for Login 'fmonteiro':
DO_User _user = new DO_User();
_user.getObject_byLogin("fmonteiro", "password"); // ToDos: here!
IDUser = _user.IDUser;


// Inserting 'administrators' Group:
DO_Group _group = new DO_Group();
_group.Name = "administrators";
_group.insObject(true); // retrieve Sequence for IDGroup
IDGroup = _group.IDGroup;
_group.Dispose(); _group = null;


// Inserting Relation User - Group:
DO_UserGroup _usergroup = new DO_UserGroup();
_usergroup.IDUser = IDUser;
_usergroup.IDGroup = IDGroup;
_usergroup.setObject(false); // Updates if exists, Inserts if not
_usergroup.Dispose(); _usergroup = null;


// Listing Users in Group:
_user.Record.Open_byGroup(
  IDGroup, 
  true
);
Console.WriteLine(
  "List of Users in Group: {0}", 
  IDGroup
);
while (_user.Record.Read()) {
  Console.WriteLine("\t{0}", _user.Login);
}
_user.Record.Close();
_user.Dispose(); _user = null;

